#Méthode qui compte le nombre de pseudos qui contiennent au moins un chiffre

		def combien_nombre(liste)

			k=0

			for i in 0..liste.length do
				
				#Utilisation de l'expression régulière correspond à Any Digit
				if liste[i] =~ /\d/
					k+=1
				end
			end
			
			return k

		end


		puts "il y a #{combien_nombre(base)} pseudos contenant au moins un chiffre"

#Méthode qui compte le nombre de fois que l'argument 's' apparait dans chaque occurence du tableau 't' donné aussi en argument

		def combien_mot(liste,mot)
			
			k=0

			for i in 0..liste.length+1 do

					#Utilisation de l'expression régulière correspond à 's' apparait une fois ou plus 
					
				if  (liste[i] =~ /#{mot}/) 
					k+=1
				end

			end
			
			return k
		end

		puts "il y a #{combien_mot(base,"Aude")} pseudos contenant Aude dans la base de données"

#Méthode qui compte le nombre de pseudos commençant par une majuscule après un @

		def combien_majuscule(liste)
			
			k=0

			for i in 0..liste.length+1 do

					if  liste[i] =~ /@[A-Z]./ 
					k+=1

				end

			end
			
			return k


		end

		puts "il y a #{combien_majuscule(base)} pseudos commençant par une majuscule dans la base entrée en argument"

